			<script type="text/javascript">
				$(function () {
					//	productDetailImgAdaptive();
					// Magnifier 2013/01/05 >>>
					var layer = $.trim("#layer734A10C66662072B235A8EA06FC16F87");
					if (layer.length === 1) return;
					var min = Math.min,
						max = Math.max,
						$layercnt = $(layer).children('.wp-product_detail_content');
					var cstyle = 'default';
					if (cstyle == 'default') {
						var magnifier = $(layer).find('.wp-magnifier_small');
						var magnifier_box = $(layer).find('.wp-magnifier_box');
						var magnifier_small_h = 0;
						var magnifier_small = $(layer).find('.wp-new-prodcuts-detail-picture-small');
						if (magnifier_small.height() > 0) magnifier_small_h = magnifier_small.height();
						if (magnifier_box.height() < magnifier.height()) magnifier_box.css('height', magnifier.height() +
							magnifier_small_h);
					}
					$('.wp-magnifier_small', $layercnt).bind("mousemove", function (e) {
						$layercnt.css("overflow", '');
						var $target = $(this),
							$prebox = $target.next('#wp-magnifier_boxbig'),
							$bigbox = $prebox.children('.wp-magnifier_big'),
							$mark = $target.children('.wp-magnifier_mark'),
							$img = $target.children('img.wp-product_detail-imgpreview'),
							bigImgW = 0,
							bigImgH = 0,
							boxW = $target.outerWidth(),
							boxH = $target.outerHeight(),
							imgW = $img.outerWidth(),
							imgH = $img.outerHeight(),
							marginLeft = magnifier_intval($img.css("marginLeft")),
							marginTop = magnifier_intval($img.css("marginTop"));
						// Big-image size
						var $bigimg = $bigbox.children('img'),
							bigimg = $bigimg[0];
						bigImgW = $bigimg.outerWidth() || bigimg.width;
						bigImgH = $bigimg.outerHeight() || bigimg.height;
						// Mark size
						var scaleX = bigImgW / imgW,
							scaleY = bigImgH / imgH,
							markW = min(imgW, magnifier_intval(imgW / scaleX)),
							markH = min(imgH, magnifier_intval(imgH / scaleY));
						$mark.width(markW).height(markH);
						markW = $mark.outerWidth();
						markH = $mark.outerHeight();
						// Mouse position
						var offset = $target.offset(),
							difX = e.pageX - offset.left,
							difY = e.pageY - offset.top;
						if ((difX < marginLeft) || (difX > boxW - marginLeft) || (difY < marginTop) || (difY > boxH - marginTop))
							return;
						var mouseX = difX - markW / 2 - marginLeft,
							mouseY = difY - markH / 2 - marginTop;
						// Max range
						var maxLeft = imgW - markW,
							maxTop = imgH - markH,
							markLeft = mouseX,
							markTop = mouseY;
						// A moving distance of the magnifier
						markLeft = (markLeft < 0) ? 0 : min(markLeft, maxLeft);
						markTop = (markTop < 0) ? 0 : min(markTop, maxTop);
						// A moving rate of the magnifier
						var perX = markLeft / imgW,
							perY = markTop / imgH;
						// Handler position
						var markL = markLeft + marginLeft,
							markT = markTop + marginTop;
						$mark.css({
							display: 'block',
							left: markL + 'px',
							top: markT + 'px'
						});
						// Image preview position
						var preboxH = $prebox.outerHeight(),
							boxMarginL = magnifier_intval($target.css("marginLeft"));
						$prebox.width(markW * scaleX).height(markH * scaleY).css({
							left: (markL + markW + boxMarginL + 5) + 'px',
							top: max(markT - (preboxH - markH) / 2, 0) + 'px'
						});
						// Image position
						$bigbox.css({
							display: 'block',
							left: -(perX * $bigbox.outerWidth()) + 'px',
							top: -(perY * $bigbox.outerHeight()) + 'px'
						});
						// Unset
						$target = $bigbox = $mark = $img = $prebox = null;
					}).bind("mouseleave", function (e) {
						$layercnt.css("overflow", 'hidden');
						$('.wp-magnifier_big, #wp-magnifier_boxbig, .wp-magnifier_mark', layer).removeAttr("style");
					});
					// <<<End
					if ('default' != 'listStyle') { /* Dyn-set right-area width 2013/07/19 */
						var $prdetail = $(layer).find('.wp-new-prodcuts-detail'),
							ltwidth = $prdetail.children('.wp-new-prodcuts-detail-left').width(),
							maxwidth = $prdetail.closest('.wp-new-prodcuts-detail-outside').width();
						if ($prdetail.length && maxwidth) {
							$prdetail.width(maxwidth).children('.wp-new-prodcuts-detail-right').width(maxwidth - ltwidth);
						}
					}
				});

				function magnifier_intval(numstr) {
					var number = parseFloat(numstr);
					if (isNaN(number)) return 0;
					return number;
				}

				function productDetailImgAdaptive() {
					// Adaptive Image
					var layerid = $.trim("layer734A10C66662072B235A8EA06FC16F87");
					if (layerid.length === 0) return;
					var $prdimg = $('img.wp-product_detail-imgpreview', '#' + layerid),
						maxw = 300,
						maxh = 300,
						_loadImg = function (img, callback) {
							if (img.complete) callback(img.width, img.height);
							else {
								img.onload = function () {
									callback(img.width, img.height);
									img.onload = null;
								};
							}
						};
					$prdimg.removeAttr("style");
					_loadImg($prdimg[0], function (width, height) {
						var tmpw = 0,
							tmph = 0;
						if (width == 300 && height == 300) {
							if ((width >= maxw) || (height >= maxh)) {
								var direct = (width >= maxw) ? (width >= height) : (height < width);
								// Horizontal
								if (direct) {
									$prdimg.width(maxw).height("auto");
									tmph = $prdimg.height();
									$prdimg.css("margin", ((maxh - tmph) / 2) + 'px');
								}
								// Vertical
								else {
									$prdimg.height(maxh).width("auto");
									tmpw = $prdimg.width();
									$prdimg.css("margin", '0 ' + ((maxw - tmpw) / 2) + 'px');
								}
							}
							//else $prdimg.css("margin", ((maxh - height) / 2)+'px '+((maxw - width) / 2)+'px');
						} else return;
					});
				}
			</script>
